import React, { Component } from 'react';
import ServersSquares from '../serversSquares/ServersSquares';
import FilterPanel from '../filterPanel/filterPanel';
import User from '../../models/Server';
import { store } from '../../redux/Store';
import { Unsubscribe } from 'redux';
import { ActionType } from '../../redux/ActionType';
import './home.css';


interface usersState {
    allUsers : User[];
    filteredUsers : User[];
}

export default class Home extends Component <any, usersState> {

    private unSubscribeStore : Unsubscribe;

    constructor(props : any) {
        super(props);

        this.state = {
            allUsers: new Array <User> (),
            filteredUsers : new Array <User> ()
        };
    }

    componentDidMount = () => {

        this.unSubscribeStore = store.subscribe(
            () => this.setState ({
                filteredUsers: store.getState().filteredUsers
            })
        );

        const users : User[] = [
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Female", true),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Female", false),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Female", false),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Female", false),
            new User("Ben Ashkenazi", 23, "Female", false),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Male", true),
            new User("Ben Ashkenazi", 23, "Male", false),
            new User("Ben Ashkenazi", 23, "Female", true),
        ];

        this.setState({
            allUsers: users,
            filteredUsers: users
        }, () => {
            store.dispatch({
                type: ActionType.GetAllUsers,
                payload: this.state.allUsers
            });

            store.dispatch({
                type: ActionType.GetFilteredUsers,
                payload: this.state.filteredUsers
            });
        });
    }


    render() {
        return (
            <div className="home">
                <FilterPanel />
                <ServersSquares filteredUsers={this.state.filteredUsers} />
            </div>
        )
    }
}
