import AppState from "./AppState";
import Action from "./Action";
import { ActionType } from "./ActionType";


export default function reducer (oldAppState : AppState, action : Action): AppState {

    // Copying the old app state, in order to modify it, based on the action
    const newAppState = {...oldAppState};

    switch (action.type) {
        case ActionType.GetAllUsers:
            newAppState.allUsers = [...action.payload];
            break;

        case ActionType.GetFilteredUsers:
            newAppState.filteredUsers = [...action.payload];
            break;

        case ActionType.ClearFilteredUsers:
            newAppState.filteredUsers = oldAppState.allUsers;
            break;
    }

    return newAppState;
}
