import React, { useRef } from 'react';
import TextField from '@material-ui/core/TextField';
import './queryFilterPanel.css';
import User from '../../models/User';
import { store } from '../../redux/Store';
import { ActionType } from '../../redux/ActionType';


export default function QueryFilterPanel() {

    const queryInput = useRef(null);

    const onQuerySearch = () => {
        const query: string = queryInput.current.children[1].children[0].value.trim() as string;
        const queryWords: string[] = query.split(" ");

        if (query === "show all") {
            store.dispatch({
                type: ActionType.GetFilteredUsers,
                payload: store.getState().allUsers
            });
        }

        if (queryWords[2] === "age") {
            filterByAge(queryWords);
        }
    }

    const filterByAge = (queryWords : string[]) => {
        const operatorStr: string = queryWords[3] as string;
        const age: number = +queryWords[4] as number;
        
        const filteredUsersFromGlobalStore: User[] = store.getState().allUsers;

        if (operatorStr === ">") {
            const filteredUsers = filteredUsersFromGlobalStore.filter( (user: User) => user.age > age );
            store.dispatch({
                type: ActionType.GetFilteredUsers,
                payload: filteredUsers
            });
        }

        else if (operatorStr === "<") {
            const filteredUsers = filteredUsersFromGlobalStore.filter( (user: User) => user.age < age );
            store.dispatch({
                type: ActionType.GetFilteredUsers,
                payload: filteredUsers
            });
        }

        else if (operatorStr === "=") {
            const filteredUsers = filteredUsersFromGlobalStore.filter( (user: User) => user.age === age );
            store.dispatch({
                type: ActionType.GetFilteredUsers,
                payload: filteredUsers
            });
        }

        else {
            alert("Illigal Operator")
        }
    }

    return (
        <div className="queryFilterSection">
            <div className="queryFilterHeaderDiv">
                <h2>Filter By a Custom Query</h2>
            </div>
            <div className="customQueryInputSection">
                <TextField className="customQueryInputField" ref={queryInput} id="outlined-basic" label="Your Custom Query" variant="outlined" />
                <button className="searchQueryButton" onClick={onQuerySearch}>Go</button>
            </div>

            <button className="resetAllFiltersButton">Reset All Filters</button>
        </div>
    )
}
