import React from 'react';
import User from '../../models/User';
import { store } from '../../redux/Store';
import './header.css';

export default function Header() {

    const getActiveUsersCount = () : number => {
        const allUsers = store.getState().allUsers;
        const allActiveUsers = allUsers.filter( (user: User) => user.active === true );
        const activeUsersCount = allActiveUsers.length;

        return activeUsersCount;
    }

    return (
        <div className="headerSection">
            <div className="headerLeft">
                <div className="adminNameSection">
                    <span>Hello, </span>
                    <span>Benoson</span>
                </div>
                <div className="activeUsersSection">
                    <span>Active Users: </span>
                    <span>{getActiveUsersCount()}</span>
                </div>
            </div>

            <div className="headerRight">
                <button className="logButton loginButton">login</button>
            </div>
        </div>
    )
}
