import React, { ChangeEvent, useRef} from 'react';
import Server from '../../models/Server';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import './filterPanel.css';

export default function FilterPanel({filteredServers} : any) {

    const isSelectedDateRadioButton = useRef(false);
    const isSelectedActiveRadioButton = useRef(false);

    const onFilterClick = (event : ChangeEvent<HTMLInputElement>) => {
        const clickedFilterInput: HTMLInputElement = event.target as HTMLInputElement;
        const clickedFilterValue: string = clickedFilterInput.value as string;

        if (clickedFilterValue === "dateFilter") {
            filterByMostRecentDate();
            isSelectedDateRadioButton.current = true;
            isSelectedActiveRadioButton.current = false;
        } else {
            isSelectedDateRadioButton.current = true;
            isSelectedActiveRadioButton.current = false;
        }

    }

    const filterByMostRecentDate = () => {
        const filteredServersByMostRecentDates = new Array();

        for (let server of filteredServers) {

            const date = server.serverCreationTime;
            const dateYear = +date.split('/')[2];
            const dateMonth = +date.split('/')[1];
            const dateDay = +date.split('/')[0];
            const creationDate = dateYear+"-"+dateMonth+"-"+dateDay;
            const fullCreationDate = new Date(creationDate);

            filteredServersByMostRecentDates.push(fullCreationDate);
        }

        const x = filteredServersByMostRecentDates.sort((a: any, b: any)=> a.getTime() - b.getTime());
        console.log(x);
        // console.log(x);
    }

    const convertDateForDisplay = () => {
        
    }

    const onClearFilters = () => {
        isSelectedDateRadioButton.current = false;
        isSelectedActiveRadioButton.current = false;
    }


    return (
        <div className="filterSection">
            <div className="filterHeaderDiv">
                <h2>Filters</h2>
            </div>
            <div className="filterButtons">
                <FormControl component="fieldset">
                    <RadioGroup row aria-label="filter" name="filters" onChange={onFilterClick} >
                        <FormControlLabel value="dateFilter" checked={isSelectedDateRadioButton.current} control={<Radio />} label="Most Recent" />
                        <FormControlLabel value="activeFilter" checked={isSelectedActiveRadioButton.current} control={<Radio />} label="Active Servers" />
                    </RadioGroup>
                </FormControl>
                
                <button className="clearFilterButton" onClick={onClearFilters}>Clear</button>
            </div>
        </div>
    )
}
