import React, { ChangeEvent } from 'react';
import userImage from '../../assets/ben_with_guitar.jpg';
import './userComponent.css';

export default function UserComponent({userDetails} : any) {
    
    const onActiveStatusChange = (event : ChangeEvent<HTMLInputElement>) => {
        const checkbox = event.target as HTMLInputElement;
        checkbox.checked = !checkbox.checked
    }

    return (
        <div className="userSquare">
            <div className="userSquareLeft">
                <div className="userImageDiv">
                    <img src={userImage} alt="user's image" className="userImage"/>
                </div>
            </div>

            <div className="userSquareCenter">
                <span>{userDetails.name}</span>
                <span>{userDetails.age}</span>
                <span>{userDetails.gender}</span>
            </div>

            <div className="userSquareRight">
                <span>Active Status </span>
                {/* <input type="checkbox" name="" id=""/> */}
                <input type="checkbox" onChange={onActiveStatusChange} checked={userDetails.active} />
                <span className="checkInstructionText">Check In Order To Suspend</span>
            </div>
        </div>
    )
}
