import React, { Component } from 'react';
import ServersSquares from '../serversSquares/ServersSquares';
import FilterPanel from '../filterPanel/filterPanel';
import Server from '../../models/Server';
import { store } from '../../redux/Store';
import { Unsubscribe } from 'redux';
import { ActionType } from '../../redux/ActionType';
import './home.css';


interface serversState {
    allServers : Server[];
    filteredServers : Server[];
}

export default class Home extends Component <any, serversState> {

    private unSubscribeStore : Unsubscribe;

    constructor(props : any) {
        super(props);

        this.state = {
            allServers: new Array <Server> (),
            filteredServers : new Array <Server> ()
        };
    }

    componentDidMount = () => {

        this.unSubscribeStore = store.subscribe(
            () => this.setState ({
                filteredServers: store.getState().filteredUsers
            })
        );

        const users : Server[] = [
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Female", true),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Female", false),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Female", false),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Female", false),
            new Server("Ben Ashkenazi", 23, "Female", false),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Male", true),
            new Server("Ben Ashkenazi", 23, "Male", false),
            new Server("Ben Ashkenazi", 23, "Female", true),
        ];

        this.setState({
            allServers: users,
            filteredServers: users
        }, () => {
            store.dispatch({
                type: ActionType.GetAllUsers,
                payload: this.state.allServers
            });

            store.dispatch({
                type: ActionType.GetFilteredUsers,
                payload: this.state.filteredServers
            });
        });
    }


    render() {
        return (
            <div className="home">
                <FilterPanel />
                <ServersSquares filteredUsers={this.state.filteredServers} />
            </div>
        )
    }
}
