import React, {useEffect, useState} from 'react';
import Server from '../../models/Server';
import { store } from '../../redux/Store';
import './header.css';

export default function Header() {

    const [activeUsersCount, setAllUserCount] = useState(0);
    
    useEffect( () => {
        const unSubscribeStore = store.subscribe(
            () => onStoreUpdate());
    }, []);

    const onStoreUpdate = () => {
        const activeUsersCount = getActiveUsersCount();
        setAllUserCount(activeUsersCount);
    }

    const getActiveUsersCount = () : number => {
        const allUsers = store.getState().allUsers;
        const allActiveUsers = allUsers.filter( (server: Server) => server.active === true );
        const activeUsersCount = allActiveUsers.length;

        return activeUsersCount;
    }

    
    return (
        <div className="headerSection">
            <div className="headerLeft">
                <div className="adminNameSection">
                    <span>Hello, </span>
                    <span>Benoson</span>
                </div>
                <div className="activeUsersSection">
                    <span>Active Users: </span>
                    <span>{activeUsersCount}</span>
                </div>
            </div>

            <div className="headerRight">
                <button className="logButton loginButton">login</button>
            </div>
        </div>
    )
}
