import React, { ChangeEvent} from 'react';
import userImage from '../../assets/ben_with_guitar.jpg';
import './serverComponent.css';


export default function UserComponent({serverDetails} : any) {
    
    const elementsKeys = new Array <number> ();

    const onActiveStatusChange = (event : ChangeEvent<HTMLInputElement>) => {
        // const checkbox = event.target as HTMLInputElement;
    }

    const generateNewElementKey = () => {
        let randomKey = Math.random() * 99999;
        let isRandomKeyExist = elementsKeys.indexOf(randomKey);

        if (isRandomKeyExist === undefined) {
            elementsKeys.push(randomKey);
        }
        else {
            while (isRandomKeyExist !== undefined) {
                randomKey = Math.random() * 99999;
                isRandomKeyExist = elementsKeys.indexOf(randomKey);
            }
        }
        return randomKey;
    }

    return (
        <div className="userSquare">
            <div className="userSquareLeft">
                <div className="userImageDiv">
                    <img src={userImage} alt="user's image" className="userImage"/>
                </div>
            </div>

            <div className="userSquareCenter">
                <span><b>Name:</b> {serverDetails.serverName}</span>
                <span><b>IP:</b> {serverDetails.serverIP}</span>
                <span><b>Hosting Company:</b> {serverDetails.serverHostingCompany}</span>
            </div>

            <div className="userSquareRight">
                <span>Online Status</span>
                <input key={generateNewElementKey()} type="checkbox" onChange={onActiveStatusChange} defaultChecked={serverDetails.active} />
                <span className="checkInstructionText">Check</span>
            </div>
        </div>
    )
}
