import React, { Component } from 'react';
import ServersSquares from '../serversSquares/ServersSquares';
import FilterPanel from "../filterPanel/filterPanel";
import Server from '../../models/Server';
import axios from 'axios';
import './home.css';


interface serversState {
    allServers : Server[];
    filteredServers : Server[];
}

export default class Home extends Component <any, serversState> {

    constructor(props : any) {
        super(props);

        this.state = {
            allServers: new Array <Server> (),
            filteredServers : new Array <Server> ()
        };
    }

    componentDidMount = async () => {

        this.getAllServersFromServer();
    }

    getAllServersFromServer = async () => {
        const serverResponse = await axios.get<Server[]>('http://localhost:3001/servers');
        const allServers: Server[] = serverResponse.data;
        this.setState({
            allServers: allServers,
            filteredServers: allServers
        });
    }

    filterServersByMostRecentDates = (filteredServersByMostRecentDates: Server[]) => {
        this.setState({
            filteredServers: filteredServersByMostRecentDates
        });
    }


    render() {
        return (
            <div className="home">
                <FilterPanel filteredServers={this.state.filteredServers} filterServersByMostRecentDates={this.filterServersByMostRecentDates} />
                <ServersSquares filteredServers={this.state.filteredServers} />
            </div>
        )
    }
}
