import React, { ChangeEvent, useEffect, useRef, useState } from 'react';
import User from '../../models/User';
import {store} from '../../redux/Store';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import { ActionType } from '../../redux/ActionType';
import './genderFilterPanel.css';

export default function GenderFilterPanel() {

    const isClearRadioButtons = useRef(false);

    const onGenderFilterClick = (event : ChangeEvent<HTMLInputElement>) => {
        const clickedGenderInput: HTMLInputElement = event.target as HTMLInputElement;
        const clickedGender: string = clickedGenderInput.value as string;
        const filteredUsers: User[] = store.getState().allUsers.filter( (user: User) => user.gender === clickedGender);
        
        store.dispatch({
            type: ActionType.GetFilteredUsers,
            payload: filteredUsers
        });

        isClearRadioButtons.current = true;
    }

    const onClearGenderFilter = () => {
        store.dispatch({
            type: ActionType.ClearFilteredUsers
        });
        isClearRadioButtons.current = true;
    }


    return (
        <div className="genderFilterSection">
            <div className="genderFilterHeaderDiv">
                <h2>Filter By Gender</h2>
            </div>
            <div className="genderFilterButtons">
                <FormControl component="fieldset">
                    <RadioGroup row aria-label="gender" name="genders" onChange={onGenderFilterClick} >
                        <FormControlLabel value="Male" control={<Radio />} label="Male" />
                        <FormControlLabel value="Female" control={<Radio />} label="Female" />
                    </RadioGroup>
                </FormControl>
                
                <button className="clearGenderFilterButton" onClick={onClearGenderFilter}>Clear</button>
            </div>
        </div>
    )
}
